mapper.select:
SELECT COUNT(1) FROM TB_ECAT_CONT_SA_HIER SA_HIER WITH(NOLOCK) JOIN TB_ECAT_CONT_SA SUB WITH(NOLOCK) ON(SUB.SA_CD = SA_HIER.SA_CD) WHERE SUB.USE_YN = 'Y' AND SA_HIER.USE_YN = 'Y' AND ( SELECT COUNT(1) FROM( SELECT IT.MATNR FROM TB_ECAT_CONT_IT IT JOIN TB_ECAT_CONT_IG_HIER IG ON(IT.IG_CD = IG.IG_CD AND IG.USE_YN = 'Y') WHERE IT.USE_YN = 'Y' AND IT.ESC_GB = 'Y' AND IG.SA_CD = SA_HIER.SA_CD
AND IT.UNIT_CD != 'CC0002'
AND IT.UNIT_CD != 'CC0002'
AND IT.UNIT_CD != 'CC0001'
) SUB )> 0
AND SA_HIER.MA_CD = #{ma_cd}

SELECT A.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY ${orderSort}) AS RN, SA_HIER.MA_CD, SUB_APP.SA_CD, SUB_APP.SA_NM, ( SELECT CONCAT(IMG.FILE_DWL_PATH, '/', IMG.FILE_THN_NM) FROM TB_ECAT_CONT_F IMG WITH(NOLOCK) WHERE SUB_APP.SA_CD = IMG.FILE_CD AND IMG.FILE_TYP = 'CC0005' ) AS MAIN_IMG, ( SELECT COUNT(1) FROM( SELECT IT.MATNR FROM TB_ECAT_CONT_IT IT JOIN TB_ECAT_CONT_IG_HIER IG ON(IT.IG_CD = IG.IG_CD AND IG.USE_YN = 'Y') WHERE IT.USE_YN = 'Y' AND IT.ESC_GB = 'Y' AND IG.SA_CD = SA_HIER.SA_CD
AND IT.UNIT_CD != 'CC0002'
AND IT.UNIT_CD != 'CC0002'
AND IT.UNIT_CD != 'CC0001'
) SUB ) AS SUB_CNT FROM TB_ECAT_CONT_SA_HIER SA_HIER WITH(NOLOCK) JOIN TB_ECAT_CONT_SA SUB_APP WITH(NOLOCK) ON(SUB_APP.SA_CD = SA_HIER.SA_CD) WHERE SUB_APP.USE_YN = 'Y' AND SA_HIER.USE_YN = 'Y' AND ( SELECT COUNT(1) FROM( SELECT IT.MATNR FROM TB_ECAT_CONT_IT IT JOIN TB_ECAT_CONT_IG_HIER IG ON(IT.IG_CD = IG.IG_CD AND IG.USE_YN = 'Y') WHERE IT.USE_YN = 'Y' AND IT.ESC_GB = 'Y' AND IG.SA_CD = SA_HIER.SA_CD
AND IT.UNIT_CD != 'CC0002'
AND IT.UNIT_CD != 'CC0002'
AND IT.UNIT_CD != 'CC0001'
) SUB )> 0
AND SA_HIER.MA_CD = #{ma_cd}
) A ORDER BY RN
OFFSET ${startIndex} * ${pageCount} ROWS FETCH NEXT ${pageCount} ROWS ONLY

SELECT TOP(1) T.SA_CD FROM TB_ECAT_CONT_IG_HIER T
WHERE T.IG_CD = #{ig_cd}
AND T.USE_YN = 'Y'

SELECT TOP(1) T.MA_CD FROM TB_ECAT_CONT_SA_HIER T
WHERE T.SA_CD = #{sa_cd}
AND T.USE_YN = 'Y'

SELECT COUNT(1) FROM ( SELECT DISTINCT IT.MATNR FROM TB_ECAT_CONT_IT IT WITH(NOLOCK) WHERE IT.USE_YN = 'Y' AND IT.ESC_GB = 'Y'
AND IT.UNIT_CD != 'CC0002'
AND IT.UNIT_CD != 'CC0002'
AND IT.UNIT_CD != 'CC0001'
AND EXISTS ( SELECT 1 FROM TB_ECAT_CONT_IT_PROP IT_PROP WHERE IT.MATNR = IT_PROP.MATNR ${filter_sch_sql} )
${sa_multi_sql}
AND IT.IG_CD IN
#{ig_cd}
) A

SELECT DISTINCT TRIM(T2.PROP_ISO) AS PROP_ISO ,T2.SCH_TYP ,T2.SQNO FROM ( SELECT * FROM TB_ECAT_CONT_IG_HIER IG_HIER WHERE 1=1
AND IG_HIER.IG_CD IN
#{ig_cd}
AND IG_CD = #{search_ig_cd}
AND IG_HIER.SA_CD IN
#{state}
AND USE_YN = 'Y' ) T1 JOIN ( SELECT * FROM TB_ECAT_PROP_SET PROP_SET WHERE SCH_YN ='Y' AND EXISTS ( SELECT 1 FROM TB_ECAT_LANG_TRAN_M LANG WHERE LANG.PROP = PROP_SET.PROP_ISO ) ) T2 ON T1.PROP_GRP_CD = T2.PROP_GRP_CD ORDER BY T2.SQNO ASC

SELECT MI.DESCR AS MORE_INFO
			,(SELECT IG.IG_NM FROM TB_ECAT_CONT_IG IG WHERE IG.IG_CD = #{search_ig_cd} AND IG.USE_YN = 'Y') AS IG_NM
FROM TB_ECAT_CONT_MI MI
WHERE 1=1
	AND MI.IG_CD = #{search_ig_cd}
	AND MI.LANG_CD = #{user_lang}

SELECT DISTINCT T2.PROP_ISO AS PROP_ISO ,T2.PROP_N_ISO AS PROP_N_ISO ,T2.DISP_PC_YN AS DISP_PC_YN ,T2.DISP_MB_YN AS DISP_MB_YN ,T2.SCH_TYP ,T2.UNIT_METR ,T2.UNIT_INCH ,T2.SQNO FROM ( SELECT * FROM TB_ECAT_CONT_IG_HIER IG_HIER WHERE 1=1
AND IG_HIER.IG_CD IN
#{ig_cd}
AND IG_CD = #{search_ig_cd}
AND IG_HIER.SA_CD IN
#{state}
AND USE_YN = 'Y' ) T1 JOIN ( SELECT * FROM TB_ECAT_PROP_SET PROP_SET WHERE SCH_YN ='Y' AND EXISTS ( SELECT 1 FROM TB_ECAT_LANG_TRAN_M LANG WHERE LANG.PROP = PROP_SET.PROP_ISO ) ) T2 ON T1.PROP_GRP_CD = T2.PROP_GRP_CD ORDER BY T2.SQNO ASC

SELECT CART.CART_NO ,CART.CART_NM FROM TB_ECAT_BASKET_M CART WHERE 1=1
AND CART.USER_ID = #{login_user_id}
AND CART.USE_YN = 'Y' ORDER BY CART.LST_DT

SELECT ASSEM.ASSEM_NO ,ASSEM.ASSEM_NM FROM TB_ECAT_ASSEM_M ASSEM WHERE 1=1
AND ASSEM.USER_ID = #{login_user_id}
AND ASSEM.USE_YN = 'Y' ORDER BY ASSEM.FIR_DT DESC

mapper.update:
UPDATE	TB_ECAT_BASKET_D
SET		LST_DT			= getdate()
		,LST_ID			= #{login_user_id}
		,QTY			= (SELECT ISNULL(MAX(A.QTY)+1, 0) FROM TB_ECAT_BASKET_D A WHERE A.MATNR = #{matnr} AND A.CART_NO = CAST (#{cart_no} AS INTEGER))
WHERE	CART_NO			= CAST (#{cart_no} AS INTEGER)
  AND	MATNR			= #{matnr}

	 UPDATE TB_ECAT_BASKET_M
     SET	LST_DT      	= getdate()
	        ,TOTAL_PRICE 	= #{total_price}
	        ,LST_ID      	= #{login_user_id}	
	 WHERE  CART_NO     	= CAST (#{cart_no} AS INTEGER)

mapper.select:
SELECT 	COUNT(1)			
FROM TB_ECAT_BASKET_D TEBD 
WHERE TEBD.CART_NO 	= #{cart_no}
  AND TEBD.MATNR 	= #{matnr}
