mapper.select:
SELECT COUNT(1)
FROM TB_ECAT_ASSEM_M M
WHERE 1=1
	
	AND M.USER_ID  = #{login_user_id}
	
	AND M.USE_YN  = 'Y'
	
	AND M.ASSEM_NO = #{assem_no}

SELECT ASBY.RN ,ASBY.ASSEM_NO ,ASBY.ASSEM_NM ,ASBY.ASSEM_DESC ,ASBY.USER_ID ,ASBY.STP_B_FILE ,ASBY.STP_D_FILE ,ASBY.DXF_FILE ,ASBY.GTC_FILE ,ASBY.P21_FILE ,ASBY.FIR_ID AS CRT_ID ,CONVERT(VARCHAR,ASBY.FIR_DT, 120) AS CRT_DT ,ASBY.LST_ID AS UPD_ID ,CONVERT(VARCHAR,ASBY.LST_DT, 120) AS UPD_DT ,( SELECT COUNT(1) FROM TB_ECAT_ASSEM_D D WHERE D.ASSEM_NO = ASBY.ASSEM_NO AND EXISTS( SELECT ASSEM_NO FROM TB_ECAT_ASSEM_M M WHERE M.ASSEM_NO = D.ASSEM_NO
AND M.USER_ID = #{login_user_id}
) AND D.USE_YN = 'Y' ) AS ITEM_CNT ,ASBY.BULD_STA ,ASBY.BULD_MSG ,CONVERT(VARCHAR,ASBY.BULD_ST, 120) AS BULD_ST ,CONVERT(VARCHAR,ASBY.BULD_ET, 120) AS BULD_ET FROM( SELECT M.* ,ROW_NUMBER() OVER (ORDER BY M.LST_DT ASC) AS RN FROM TB_ECAT_ASSEM_M M WHERE 1=1
AND M.USER_ID = #{login_user_id}
AND M.USE_YN ='Y'
AND M.ASSEM_NO = #{assem_no}
) ASBY
ORDER BY FIR_DT DESC
OFFSET #{startIndex} ROWS FETCH NEXT #{pageCount} ROWS ONLY

SELECT COUNT(1) FROM TB_ECAT_ASSEM_D D WHERE D.ASSEM_NO = CAST(#{assem_no} AS INTEGER) AND EXISTS( SELECT ASSEM_NO FROM TB_ECAT_ASSEM_M M WHERE M.ASSEM_NO = D.ASSEM_NO
AND M.USER_ID = #{login_user_id}
) AND D.USE_YN = 'Y'

SELECT D.ASSEM_NO ,D.MATNR ,(SELECT IT.IG_CD FROM TB_ECAT_CONT_IT IT WHERE IT.MATNR = D.MATNR) AS IG_CD ,D.DESIGNATION ,D.GRADE ,D.FIR_ID AS CRT_ID ,D.FIR_DT AS CRT_DT ,D.LST_ID AS UPD_ID ,D.LST_DT AS UPD_DT ,(SELECT CONCAT(IMG.FILE_DWL_PATH, '/', IMG.FILE_THN_NM) FROM TB_ECAT_CONT_F IMG WHERE 1=1 AND IMG.FILE_CD = D.MATNR AND IMG.USE_YN = 'Y' AND IMG.FILE_TYP = 'CC0017') AS IT_IMG ,(SELECT COUNT(1) FROM TB_ECAT_ASSEM_LOC LOC WHERE LOC.ASSEM_NO = #{assem_no} AND LOC.MATNR = D.MATNR ) AS QUANTITY ,(SELECT M.BULD_STA FROM TB_ECAT_ASSEM_M M WHERE M.ASSEM_NO = #{assem_no}) AS BULD_STA FROM TB_ECAT_ASSEM_D D JOIN TB_ECAT_CONT_IT U ON U.MATNR = D.MATNR WHERE 1=1 AND D.ASSEM_NO = #{assem_no} AND EXISTS( SELECT ASSEM_NO FROM TB_ECAT_ASSEM_M M WHERE M.ASSEM_NO = D.ASSEM_NO
AND M.USER_ID = #{login_user_id}
) AND D.USE_YN = 'Y' AND U.UNIT_CD IN (#{search_unit_cd},'CC0003') ORDER BY D.LST_DT

mapper.delete:
        DELETE
        FROM   TB_ECAT_ASSEM_D
        WHERE  ASSEM_NO 	= CAST (#{assem_no} AS INTEGER)
        
          AND  MATNR 	= #{matnr}
        
          AND  EXISTS( SELECT 	ASSEM_NO
	     			   				FROM 	TB_ECAT_ASSEM_M M
	     			   				WHERE	M.ASSEM_NO = TB_ECAT_ASSEM_D.ASSEM_NO
			  			  			
	     							AND		M.USER_ID = #{login_user_id}
	     							
	     						)

mapper.update:
    UPDATE TB_ECAT_ASSEM_M
    SET		LST_DT      	= getdate()
    		,LST_ID      	= #{login_user_id}
	    
	    	,ASSEM_NM    	= #{assem_nm}
	    
	    
	    	,ASSEM_DESC   	= #{assem_desc}
	    
	    
	    	,STP_B_FILE   	= #{stp_b_file}
	    
	    
	    	,STP_D_FILE   	= #{stp_d_file}
	    
	    
	    	,DXF_FILE   	= #{dxf_file}
	    
	    
	    	,GTC_FILE   	= #{gtc_file}
	    
	    
	    	,P21_FILE   	= #{p21_file}
	    
          	
    WHERE  ASSEM_NO     	= CAST (#{assem_no} AS INTEGER)

mapper.select:
SELECT CART.CART_NO ,CART.CART_NM FROM TB_ECAT_BASKET_M CART WHERE 1=1
AND CART.USER_ID = #{login_user_id}
AND CART.USE_YN = 'Y' ORDER BY CART.LST_DT

WITH CTG_TREE AS ( SELECT 1 AS LVL, CONVERT(VARCHAR(MAX),CLASS_ID) AS PLM_DEPTH, UID, PARENT_UID, CLASS_ID, CNAME FROM IF_PLM_CLASS WITH(NOLOCK) WHERE CLASS_ID = 'CTL' UNION ALL SELECT CT.LVL + 1 AS LVL, CASE WHEN C2.CLASS_ID IS NOT NULL AND C2.CLASS_ID <> '' THEN CONVERT(VARCHAR(MAX),CONCAT(CT.PLM_DEPTH,'>',C2.CLASS_ID)) ELSE '' END PLM_DEPTH, C2.UID, C2.PARENT_UID, C2.CLASS_ID, C2.CNAME FROM IF_PLM_CLASS C2 WITH(NOLOCK), CTG_TREE CT WHERE CT.UID = C2.PARENT_UID ) SELECT GTC_SORT, GTC_DEPTH, GTC_VD_NODE_NM, dbo.FN_REMOVE_SPECIAL_CHARS(GTC_VD_RT) GTC_VD_RT, GTC_VD_ID, GTC_VD_PID, MATNR, DESIGNATION FROM ( SELECT CTR.LVL AS GTC_SORT, CTR.PLM_DEPTH AS GTC_DEPTH, REPLICATE(' ', 4*(CTR.LVL-1)) + CTR.CNAME AS GTC_VD_NODE_NM, ( SELECT value FROM ( SELECT ROW_NUMBER() OVER(ORDER BY (SELECT 1)) AS RNUM, * FROM STRING_SPLIT(( CASE WHEN CTR.UID = PLMD.CLASS_UID THEN CTR.CLASS_ID ELSE '' END ), '_') ) T1 WHERE RNUM = 1 ) AS GTC_VD_RT, CASE WHEN CTR.UID = PLMD.CLASS_UID THEN CTR.CLASS_ID ELSE '' END GTC_VD_ID, (SELECT CLASS_ID FROM IF_PLM_CLASS B WHERE CTR.PARENT_UID = B.UID) AS GTC_VD_PID, ECIT.MATNR, PLMD.CNO AS DESIGNATION FROM CTG_TREE CTR WITH(NOLOCK) LEFT OUTER JOIN IF_PLM_DSGN PLMD WITH(NOLOCK) ON CTR.UID = PLMD.CLASS_UID LEFT OUTER JOIN TB_ECAT_CONT_IT ECIT WITH(NOLOCK) ON PLMD.CNO = ECIT.DESIGNATION ) MG WHERE 1=1
AND MG.MATNR = #{matnr}
ORDER BY MG.GTC_DEPTH

	SELECT * FROM TB_ECAT_CONT_IT_PROP WITH(NOLOCK)
	WHERE SYMBOL IN ('CCMS','CCWS','IIC','DCON-MS','DCON-WS','DCONMS','DCONWS')
	
	
    
      AND MATNR = #{matnr}

	SELECT FILE_PHY_PATH
      FROM TB_ECAT_CONT_F WITH(NOLOCK)
     WHERE 1=1
	   AND FILE_CD = #{file_cd}
	   AND FILE_TYP = #{file_typ}

		SELECT COUNT(*) FROM TB_ECAT_CONT_F WITH(NOLOCK)
			WHERE WERKS = #{werks} 
			  AND FILE_CD = #{file_cd}
  		  	  AND FILE_TYP = #{file_typ}

mapper.delete:
        DELETE TB_ECAT_CONT_F
		 WHERE WERKS = #{werks}
		   AND FILE_CD = #{file_cd}
           AND FILE_TYP = #{file_typ}

insert.selectKey:
			SELECT COUNT(*) AS FILE_CNT FROM TB_ECAT_CONT_F
			WHERE FILE_CD = #{file_cd}
  		  	  AND FILE_TYP = #{file_typ}

insert.if:
INSERT INTO TB_ECAT_CONT_F (
       WERKS
     , FILE_CD
     , FILE_SEQ
     , FILE_TYP
     , FILE_PHY_PATH
     , FILE_DWL_PATH
     , FILE_ORG_NM
     , FILE_THN_NM
     , USE_YN
     , FIR_ID
     , FIR_DT
) VALUES (
       #{werks}
     , #{file_cd}
     , (SELECT ISNULL(MAX(FILE_SEQ)+1, 1) FROM TB_ECAT_CONT_F WHERE WERKS = #{werks} AND FILE_CD = #{file_cd})
     , #{file_typ}
     , #{file_phy_path}
     , #{file_dwl_path}
     , #{file_org_nm}
     , #{file_thn_nm}
     , #{use_yn}
     , #{login_user_id}
     , GETDATE()
)

mapper.delete:
