				#{matnr}

mapper.select:
SELECT LANG_CD
  FROM TB_ECAT_LANG WITH(NOLOCK)
 WHERE LANG_PACK = #{lang_pack}

mapper.update:
UPDATE	TB_ECAT_BASKET_D
SET		LST_DT			= getdate()
		,LST_ID			= #{login_user_id}
		,QTY			= (SELECT ISNULL(MAX(A.QTY)+1, 0) FROM TB_ECAT_BASKET_D A WHERE A.MATNR = #{matnr} AND A.CART_NO = CAST (#{cart_no} AS INTEGER))
WHERE	CART_NO			= CAST (#{cart_no} AS INTEGER)
  AND	MATNR			= #{matnr}

	 UPDATE TB_ECAT_BASKET_M
     SET	LST_DT      	= getdate()
	        ,TOTAL_PRICE 	= #{total_price}
	        ,LST_ID      	= #{login_user_id}	
	 WHERE  CART_NO     	= CAST (#{cart_no} AS INTEGER)

mapper.select:
SELECT 	COUNT(1)			
FROM TB_ECAT_BASKET_D TEBD 
WHERE TEBD.CART_NO 	= #{cart_no}
  AND TEBD.MATNR 	= #{matnr}

SELECT IG.IG_CD FROM TB_ECAT_CONT_IG IG WHERE 1 = 1 AND IG.IG_CD IN ( SELECT IT.IG_CD FROM TB_ECAT_CONT_IT IT WHERE 1 = 1 AND IT.MATNR IN
#{matnr}
AND IT.USE_YN = 'Y' AND IT.ESC_GB = 'Y' GROUP BY IT.IG_CD ) AND IG.USE_YN = 'Y'

mapper.update:
UPDATE TB_ECAT_ASSEM_M SET LST_DT = getdate() , LST_ID = #{login_user_id} , BULD_STA = #{buld_sta}
, BULD_ST = getdate()
, BULD_ET = getdate()
, BULD_MSG = #{buld_msg}
WHERE ASSEM_NO = CAST (#{assem_no} AS INTEGER)

mapper.delete:
    DELETE FROM TB_ECAT_ASSEM_LOC
    WHERE  ASSEM_NO = CAST (#{assem_no} AS INTEGER)	    
    
	AND MATNR = #{matnr}

mapper.select:
SELECT LOC.ASSEM_NO ,LOC.MATNR ,LOC.LOCATION FROM TB_ECAT_ASSEM_LOC LOC WHERE LOC.ASSEM_NO = CAST (#{assem_no} AS INTEGER)
AND LOC.FIR_ID = #{login_user_id}
ORDER BY LOC.LOCATION
